    //Banner Authentication with LDAP - middlware auth

    var url = $rootScope.middlewareServerUrl + "/bannermobile2/services/authenticate/login";
    var data = {};
    if (window.device) {
        data = "username=" + uname + "&password=" + password;
    } else {
        url = "/websimulator/json?url=" + url;
        data = {
            method: "POST",
            body: "username=" + uname + "&password=" + password
        };
    }
    var authUrl = url;
    var authData = data;
    $rootScope.generateST = function(tgt, service, callback) {
        //Bypassing the ST as there is no service ticket required here..
        callback(tgt);
    };

    $rootScope.postAPI = function(endpoint, postdata, callback) {
        $.blockUI();
        $rootScope.generateST($rootScope.ticket, endpoint, function(st) {
            var serviceurl = endpoint + "?ticket=" + st;
            var url = "";
            var proxyMethod = "POST";
            var proxyData = postdata;
            if (window.device) {
                url = serviceurl;
            } else {
                url = "/websimulator/json?url=" + serviceurl;
                proxyMethod = "POST";
                proxyData = {
                    method: "POST",
                    body: postdata
                }
            }
            $http({
                method: proxyMethod,
                url: url,
                data: proxyData
            }).
            success(function(data, status, headers, config) {
                $.unblockUI();
                callback(data, status, headers, config);
            }).
            error(function(data, status, headers, config) {
                $.unblockUI();
                //alert("Error in service call : " + data + " Status : " + status);
                if (status == 403) { //Ticket expired.. need to relogin
                    $http.post(authUrl, authData).success(function(data, status, headers, config) {
                        authSuccess(data);
                        $rootScope.getAPI(endpoint, callback);
                    }).error(function(data, status, headers, config) {
                        authError(data);

                    });
                }

            });

        });

    };

    $rootScope.getAPI = function(endpoint, callback) {
        $.blockUI();
        $rootScope.generateST($rootScope.ticket, endpoint, function(st) {
            var serviceurl = endpoint + "?ticket=" + st;
            var url = "";
            var proxyMethod = "GET";
            var proxyData = {};
            if (window.device) {
                url = serviceurl;
            } else {
                url = "/websimulator/json?url=" + encodeURIComponent(serviceurl);
                proxyMethod = "POST";
                proxyData = {
                    method: "GET"
                }
            }
            $http({
                method: proxyMethod,
                url: url,
                data: proxyData
            }).
            success(function(data, status, headers, config) {
                $.unblockUI();
                callback(data, status, headers, config);
            }).
            error(function(data, status, headers, config) {
                $.unblockUI();
                //alert("Error in service call : " + data + " Status : " + status);
                if (status == 403) { //Ticket expired.. need to relogin
                    $http.post(authUrl, authData).success(function(data, status, headers, config) {
                        authSuccess(data);
                        $rootScope.getAPI(endpoint, callback);
                    }).error(function(data, status, headers, config) {
                        authError(data);

                    });
                }

            });

        });

    };




    $.blockUI({
        message: '<div style="margin: 2px; vertical-align: middle; display: inline-block"><i class="icon-cog icon-spin icon-4x"></i><h3>Authenticating</h3></div>'
    });

    $http.post(url, data).success(function(data, status, headers, config) {
        success(data);
        $.unblockUI();
    }).error(function(data, status, headers, config) {
        error(data);
        $.unblockUI();
    });

}

pages.pageprocessor:
function pageprocessorObopay(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);
  $("#fixedHeaderContent").hide();

  //var profiledata = $.jStorage.get("user"); // ------  For U8 Apps
  var profiledata = $.jStorage.get("userdetails"); // --- For U7 Apps

  if (!profiledata) {
    navigator.notification.alert('User not logged in.', null, '', 'Ok');
    return;
  }

  $scope.phone = profiledata.phone ? parseInt(profiledata.phone) : 0;
  $rootScope.phone = $scope.phone.toString() /*"8130085294"*/ ;
  $rootScope.name = (profiledata.firstname ? profiledata.firstname : profiledata.firstName) + " " + (profiledata.lastname ? profiledata.lastname : profiledata.lastName);
  $rootScope.email = profiledata.email;
  $rootScope.image = '';
  $scope.showDialog = false;
  $scope.paymentMode = false;

  $scope.choice = '';

  if (profiledata.img == '' || profiledata.img == undefined)
    $rootScope.image = 'images/profileImage.png';
  else
    $rootScope.image = profiledata.img;

  const productionEndpoint = 'https://kryptosda.kryptosmobile.com/feepayment';

  $rootScope.callApi = function(proxyMethod, proxyData, url, callback) {
    try {
      let request = {
        method: proxyMethod,
        url: url,
        data: proxyData,
      };
      $http(request).then(function(data) {
        callback(null, data.data);
      }, function(err) {
        alert("Err1:" + JSON.stringify(err));
        callback(err, null);
      });
    } catch (e) {}
  };

  $rootScope.initSDK = (userMobileNumber, callback) => {

    $.blockUI({
      message: '<div id="floatingBarsG"><div class="blockG" id="rotateG_01"></div><div class="blockG" id="rotateG_02"></div><div class="blockG" id="rotateG_03"></div><div class="blockG" id="rotateG_04"></div><div class="blockG" id="rotateG_05"></div><div class="blockG" id="rotateG_06"></div><div class="blockG" id="rotateG_07"></div><div class="blockG" id="rotateG_08"></div></div><div></div>'
    });

    const getAuthKeyUrl = `${productionEndpoint}/getAuthKey?mobile=${userMobileNumber}`;
    $rootScope.callApi('GET', {}, getAuthKeyUrl, function(err, authData) {
      if (err) {
        $.unblockUI();
        navigator.notification.alert('Unable to process, please try again.', null, '', 'Ok');
      } else {
        if (authData.data.authKey) {
          // alert("auth >>> " + authData.data.authKey)
          userMobileNumber = "+91" + userMobileNumber;
          Obopayments.initialize("lakshmibai-prod", userMobileNumber, authData.data.authKey,
            'I-MADE', "https://kryptosda.kryptosmobile.com/kryptosds/imagegallery/55020caa-45a5-4813-81b3-9b1f9210ba5c.png",
            '#fe9901',
            function(sdkResp) {
              $.unblockUI();
              // alert('SDK initialize' + JSON.stringify(sdkResp));
              if (sdkResp.code == "FAILURE" && sdkResp.data.errorCode == 'USER_NOT_ACTIVATED') {
                callback(sdkResp.data, authData.data);
              } else
              if (sdkResp.code == "FAILURE") {
                alert("SDK initialization failed: " + sdkResp.data.errorMessage);
              } else {
                // alert('SDK initialize successfully!');
                callback(sdkResp.data, authData.data);
              }
            });
        } else {
          $.unblockUI();
          alert("get auth Key error" + JSON.stringify(authData));
        }
      }
    });
  }


  $rootScope.activateUser = (cb) => {
    Obopayments.activateUser({}, (res) => {
      cb();
    });
  }


  $scope.goto = () => {
    $location.path('app/Obopay/ObopayPage3');
  }


  /* Variable*/
  $scope.hasAccesstoken = true;
  $scope.requestForOtp = false;
  $scope.enterOtp = false;
  $scope.otp = '';
  $rootScope.walletBalance = '';
  $scope.otpRequestData = {};


  const getBalance = () => {
    Obopayments.getBalance(function(data) {
      // alert("get balance >>" + JSON.stringify(data));
      if (data.code == "FAILURE" && data.data.errorCode == "SDK_INIT_PENDING") {
        $rootScope.initSDK($rootScope.phone, function() {
          getBalance();
        });
      }

      if (data.code == "FAILURE" && data.data.errorCode == "USER_NOT_ACTIVATED") {
        $rootScope.activateUser(function() {
          getBalance();
        })
      }

      if (data.code == "SUCCESS") {
        $scope.walletBalance = data.data.primaryBalance;
        $rootScope.walletBalance = data.data.primaryBalance;
        $location.path("/app/Obopay/ObopayPage3");
        $scope.$apply();
      }
    });
  }

  if (profiledata.OboPayWalletExist) {
    // getBalance();
    $rootScope.phone = profiledata.phone;
    $location.path("/app/Obopay/ObopayPage3");
  } else {
    $scope.hasAccesstoken = false;
    $scope.requestForOtp = true;
    $scope.enterOtp = false;
  }



  $scope.requestOtp = function() {
    let regex = /^[0]?[6789]\d{9}$/;
    let number = ($scope.phone).toString();
    if (!number.match(regex)) {
      alert("Please enter correct number!");
      return
    }

    $rootScope.phone = ($scope.phone).toString();
    $scope.phone = $rootScope.phone;

    $rootScope.initSDK($rootScope.phone, (sdkResponse, authResponse) => {
      //SDK - {"errorCode" : "",errorMessage: ""}
      //Auth - {"authKey":"","userStatus":"","cardStatus":""}
      if (authResponse.userStatus == "NOT_REGISTERED") {
        $location.path('app/Obopay/ObopayPage2');
        $scope.$apply();
      } else if (authResponse.userStatus == "KYC_PENDING" || authResponse.userStatus == "REGISTERED") {
        profiledata.OboPayWalletExist = true;
        profiledata.phone = $rootScope.phone;
        //$.jStorage.set("user", profiledata);
        $.jStorage.set("userdetails", profiledata);
        $scope.hasAccesstoken = true;
        $scope.requestForOtp = false;
        $scope.enterOtp = false;
        $location.path("/app/Obopay/ObopayPage3");
        $scope.$apply();
        // getBalance();
      }
    });
  };

  $scope.payMoney = () => {

    alert("Paying through mobile number");

    let sendMoneyParams = {
      mobileNo: "+918867983054",
      amount: 15,
      transMsg: "testing"
    }

    Obopayments.sendMoney(sendMoneyParams, (res) => {
      //alert("Send money response" + JSON.stringify(res));
    })


  }


  $scope.addMoney = function() {
    let addMoneyParams = {
        amount: 2
      }
      //alert("adding money" + JSON.stringify(addMoneyParams));
    Obopayments.addMoney(addMoneyParams, (res) => {
      //alert("add money respo>>" + JSON.stringify(res))
      // getBalance();
      $location.path("/app/Obopay/ObopayPage3");
    })
  }

  $scope.openFood = function() {
    $location.path('app/BigDeal/BigDeal');
  }

  $scope.otpChange = function() {

    if ($scope.otp.length > 4)
      $scope.otp = $scope.otp.charAt(0) + $scope.otp.charAt(1) + $scope.otp.charAt(2) + $scope.otp.charAt(3);


    if ($scope.otp.charAt(0) != '') {
      $("#firstChar").html($scope.otp.charAt(0)).addClass('hasCharClass').removeClass('noCharClass');
    } else {
      $("#firstChar").html('&nbsp;').addClass('noCharClass').removeClass('hasCharClass');
    }

    if ($scope.otp.charAt(1) != '') {
      $("#secondChar").text($scope.otp.charAt(1)).addClass('hasCharClass').removeClass('noCharClass');
    } else {
      $("#secondChar").html('&nbsp;').addClass('noCharClass').removeClass('hasCharClass');
    }

    if ($scope.otp.charAt(2) != '') {
      $("#thirdChar").text($scope.otp.charAt(2)).addClass('hasCharClass').removeClass('noCharClass');
    } else {
      $("#thirdChar").html('&nbsp;').addClass('noCharClass').removeClass('hasCharClass');
    }

    if ($scope.otp.charAt(3) != '') {
      $("#forthChar").text($scope.otp.charAt(3)).addClass('hasCharClass').removeClass('noCharClass');
    } else {
      $("#forthChar").html('&nbsp;').addClass('noCharClass').removeClass('hasCharClass');
    }

  }

  $scope.popUpMethod = function() {
    $scope.showDialog = !$scope.showDialog;
  }

  $scope.howtopay = function() {
    $scope.paymentMode = !$scope.paymentMode;
  }

  $scope.scanQRCode = function() {
    cordova.plugins.barcodeScanner.scan(
      function(result) {
        if (!result.text)
