    });
  };
  $rootScope.unloadBlocker = function() {
    $.unblockUI();
  };

  alert("$rootScope--> "+ $rootScope.BASE_URL );
  var userCred = $.jStorage.get("userCredentials");
  alert("userCred --> " + JSON.stringify(userCred));
  if (!userCred) {

    $location.path('app/SignIn2/SignIn2');
  } else {
    $rootScope.loadBlocker();

    userCred = JSON.parse(userCred);

    $http.get($rootScope.BASE_URL + "/auth/isLoggedIn").then(function(res) {
       alert(" $rootScope.isLoggedIn --> "+  res);
      $rootScope.unloadBlocker();
      $rootScope.loginStatus = res.data;
    }, function(err, status) {
        alert("Error--> "+JSON.stringify(err) + " -- "+ status);
      if (err.status == 401) {
        // unauthenticated user .. login user 
        $http.post($rootScope.BASE_URL + "/auth/login/krypto", userCred).then(function(res) {
          $rootScope.unloadBlocker();


        }, function(err, status) {
          $rootScope.unloadBlocker();

          alert("something went wrong");
        });
      }
    });
  }



  $("#appContent").append(data1);

  var offerBoxCSS = "col-xs-6 col-sm-6 col-md-6 col-lg-6 offer-box";
  var offerBoxNew = "col-xs-12 col-sm-12 col-md-12 col-lg-12 offer-box";
  var offerSummaryOld = "col-xs-12 col-sm-12 col-md-12 col-lg-12 offer-summary";
  var offerSummaryNew = "col-xs-6 col-sm-6 col-md-6 col-lg-6 offer-summary";
  var offerDetailsOld = "col-xs-12 col-sm-12 col-md-12 col-lg-12 offer-details";
  var offerDetailsNew = "col-xs-6 col-sm-6 col-md-6 col-lg-6 offer-details";
  var _expandand = false;
  $scope.openDescription = function($event) {

    var $currEle = $($event.target);
    var $offerContainer = $currEle.closest(".offer-box");
    var $offerDetails = $offerContainer.find(".offer-details");
    var $offerSummary = $offerContainer.find(".offer-summary");
    var $vDivider = $offerContainer.find(".v-divide");
    if (_expandand) {
      $offerContainer.attr("class", offerBoxCSS);
      $offerDetails.attr("class", offerDetailsOld);
      $offerSummary.attr("class", offerSummaryOld);
      $offerDetails.addClass("hide");
      $vDivider.addClass("hide");
    } else {
      $offerContainer.attr("class", offerBoxNew);
      $offerDetails.attr("class", offerDetailsNew);
      $offerSummary.attr("class", offerSummaryNew);
      $offerDetails.removeClass("hide");
      $vDivider.removeClass("hide");
    }
    _expandand = !_expandand;
  };



  $rootScope.myAccount = function() {
    $location.path('app/campusPay/campusPayPage4');
  };
  $scope.activateOffer = function(offer) {
    $rootScope.selectedOffer = offer;
    //$rootScope.$apply();

    $location.path('app/campusPay/campusPayPage2');
  };

  var user;

  if (!user) {
    if ($.jStorage.get("profileData")) {
      user = {};

      var profileData = $.parseJSON($.jStorage.get("profileData"));
      user.user_id = profileData.user_id;
      user.email = profileData.register_email;
      user.name = profileData.register_name;
      user.college = profileData.register_college;
      user.roll = profileData.register_roll;
      if (profiledata.img == undefined || profiledata.img == null) {
        user.ProfilePic = 'images/profileImage.png'
      } else {
        user.ProfilePic = profiledata.img;
      }
    }
  }

  var userDetails = $.jStorage.get("userdetails");
  if (userDetails) {
    user = {};
    user.user_id = userDetails._id;
    user.email = userDetails.email;
    user.name = userDetails.firstname + userDetails.lastname;
    user.roll = userDetails.rollno;
  }

  if (user && !user.ProfilePic) {
    user.ProfilePic = 'images/profileImage.png';
  }


  //***** DUmmy user
  /* user = {};
   user.user_id = "5979b6ae276be67240419688";
   user.email = "hiiibdh@gmail.com";
   user.name = "harendra kumar";
   user.roll = "16040527023";
   */
  //***********

  if (!user) {
    $location.path('app/SignIn2/SignIn2');
    return;
  }
  $rootScope.user = user;
  $rootScope.loadBlocker();
  $http.get($rootScope.BASE_URL + "/offer/active").then(function(res) {
    $scope.offers = res.data;
    $rootScope.unloadBlocker();
  }, function(err) {

    $rootScope.unloadBlocker();
    console.log(err);
    //handle errorr
  });
  var walletUrl = $rootScope.BASE_URL + "/wallet/get?userid=" + user.user_id;
  $rootScope.loadBlocker();
  $http.get(walletUrl).then(function(res) {
    $rootScope.wallet = res.data;
    $rootScope.unloadBlocker();
  }, function(err) {
    $rootScope.unloadBlocker();
    console.log(err);
    //handle error
  });

  $scope.getExpiryText = function(offer) {
    var milliesInADay = 1000 * 60 * 60 * 24;
    var milliesInHour = 1000 * 60 * 60;
    var currentDate = new Date();
    var offerEndDate = new Date(offer.endDate);
    var timeDiff = offerEndDate.getTime() - currentDate.getTime();
    var diffDays = Math.round(timeDiff / milliesInADay);
    var dayMillies = timeDiff % milliesInADay;
    var hours = Math.round(dayMillies / milliesInHour);
    var str = `Offer Expires in ${diffDays} days, ${hours} hours`;
    return str;
  };
}

