                          for (var i = 0; i < $scope.studentList.length; i++) {
                            $scope.studentList[i].status = "absent";
                          }
                          $rootScope.studentList = $scope.studentList;
                          $location.path('/app/FacultyAttendance/FacultyAttendancePage2');
                        } else {
                          $scope.markedStudentList = data.studentsList;
                          for (var i = 0; i < $scope.studentList.length; i++) {

                            var stuMarkedAtt = false;
                            for (var j = 0; j < $scope.markedStudentList.length; j++) {
                              if (query.branchName == "Add On") {
                                if ($scope.studentList[i].email.toLowerCase() == $scope.markedStudentList[j].email.toLowerCase()) {
                                  $scope.studentList[i].status = "present";
                                  stuMarkedAtt = true;
                                  break;
                                }
                              } else {

                                if ($scope.studentList[i].rollno == $scope.markedStudentList[j].rollNo || $scope.studentList[i].unirollno == $scope.markedStudentList[j].rollNo) {
                                  $scope.studentList[i].status = "present";
                                  stuMarkedAtt = true;
                                  break;
                                }
                              }
                            }

                            if (!stuMarkedAtt) {
                              $scope.studentList[i].status = "absent";
                            }
                          }
                          $rootScope.studentList = $scope.studentList;
                          //$rootScope.$apply();
                          $location.path('/app/FacultyAttendance/FacultyAttendancePage2');

                        }
                      }).
                      error(function(data, status, headers, config) {
                        $.unblockUI();

                        alert(JSON.stringify(data));
                      });
                    }
                  }).
                  error(function(data, status, headers, config) {
                    $.unblockUI();

