            $scope.$apply();
            $location.path('/app/FacultyAttendance/FacultyAttendancePage2');

          }
        }).
        error(function(data, status, headers, config) {
          $.unblockUI();
          alert(JSON.stringify(data));
        });
      }
    }).
    error(function(data, status, headers, config) {
