        $(childindex).prop("checked", true);
        countArray[parentindex] = 1;
        console.log(countArray);

    };


    $scope.changeCheck = function (choice, name, parentindex, childindex) {
        if (choice.selected == true) {
            console.log("selected");
            console.log(countArray);
            var count = countArray[parentindex];
            count = count + 1;
            console.log("count=" + count);
