                        '',            // title
                        'Ok'          // buttonLabels
                    );

                } else {
                    alert("You can select atmost " + name.maxSelect + " toppings");
                }


            } else {
                countArray[parentindex] = count;
                console.log(countArray);
            }
        }
        else {
            console.log("unselected");
            console.log(countArray);
            var count = countArray[parentindex];
            console.log("count=" + count);
            count = count - 1;
            countArray[parentindex] = count;
            console.log(countArray);
        }

