                        navigator.notification.alert($rootScope.ChoicesArray[i].requiredDesc, null, $rootScope.ChoicesArray[i].choiceDesc, 'Ok');

                    }
                    else {
                        alert($rootScope.ChoicesArray[i].choiceDesc);
                    }
                    break;
                    return;


                }

            }
        }else{
            callnext = true;
        }

        if (callnext == true) {
            gotoNext();
        }


    };
    function gotoNext() {

        $scope.payableAmount = parseFloat($rootScope.restCategoryItemSelect.actualPrice);
        $scope.totalDiscount = parseFloat($rootScope.restSelect.discount) + parseFloat($rootScope.restCategorySelect.CategoryDisocunt) + parseFloat($rootScope.restCategoryItemSelect.discount);

