function pageprocessorBigDealPage11(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);
    console.log(JSON.stringify(JSON.parse(angular.toJson($rootScope.cartItems))));
    $rootScope.back = function () {
        $location.path("/app/BigDeal/BigDealPage8");

    };
    /*Analytics for Cart page*/
    var postUrl = "https://kryptosda.kryptosmobile.com/kryptosds/v1/bigdeal/updatePageCountAnalytics";
    var postData = {
        "tenant": $rootScope.metadata.tenantid,
        "page": "CartPage"
    };
    $http({
        url: postUrl,
        method: 'POST',
        data: postData
    }).then(function (res) {
        console.log(JSON.stringify(res.data));

    }, function (err) {
        $scope.err = err;
    });

    $scope.vat = $rootScope.restSelect.vat;
    $scope.serviceTax = $rootScope.restSelect.serviceTax;
    $scope.serviceCharge = $rootScope.restSelect.serviceCharge;
    $scope.cartItems = $rootScope.cartItems;
    $scope.subtotal = 0.0;
    for (i = 0; i < $scope.cartItems.length; i++) {
        $scope.subtotal = $scope.subtotal + parseFloat($scope.cartItems[i].itemAmount);
    }
    $scope.subtotal = $scope.subtotal.toFixed(2);
    $scope.discount = $rootScope.restSelect.discount;
    $scope.discountamount = (parseFloat(($scope.discount) * parseFloat($scope.subtotal)) / 100).toFixed(2);
    $scope.vatAmount = (($scope.subtotal - $scope.discountamount) * $scope.vat).toFixed(2);
    $scope.serviceTaxAmount = (($scope.subtotal - $scope.discountamount) * $scope.serviceTax).toFixed(2);
    $scope.serviceChargeAmount = (($scope.subtotal - $scope.discountamount) * $scope.serviceCharge).toFixed(2);

    $scope.payableamount = (parseFloat($scope.subtotal) - parseFloat($scope.discountamount) + parseFloat($scope.vatAmount)+ parseFloat($scope.serviceTaxAmount)+ parseFloat($scope.serviceChargeAmount)).toFixed(2);
    if ($scope.payableamount < $rootScope.restSelect.minOrderAmt) {
        $scope.showPay = false;
    }
    else {
        $scope.showPay = true;
    }

    function calculate() {
        $scope.subtotal = 0.0;
        for (i = 0; i < $scope.cartItems.length; i++) {
            $scope.subtotal = $scope.subtotal + parseFloat($scope.cartItems[i].itemAmount);
        }
        $scope.subtotal = $scope.subtotal.toFixed(2);
        $scope.discount = $rootScope.restSelect.discount;
        $scope.discountamount = (parseFloat(($scope.discount) * parseFloat($scope.subtotal)) / 100).toFixed(2);
        $scope.vatAmount = (($scope.subtotal - $scope.discountamount) * $scope.vat).toFixed(2);
        $scope.serviceTaxAmount = (($scope.subtotal - $scope.discountamount) * $scope.serviceTax).toFixed(2);
        $scope.serviceChargeAmount = (($scope.subtotal - $scope.discountamount) * $scope.serviceCharge).toFixed(2);

        $scope.payableamount = (parseFloat($scope.subtotal) - parseFloat($scope.discountamount) + parseFloat($scope.vatAmount)+ parseFloat($scope.serviceTaxAmount)+ parseFloat($scope.serviceChargeAmount)).toFixed(2);
        if ($scope.payableamount < $rootScope.restSelect.minOrderAmt) {
            $scope.showPay = false;
        }
        else {
            $scope.showPay = true;

        }
        $scope.cartItems = $rootScope.cartItems;
        $scope.$apply();

    }

    $scope.itemRemove = function (index) {

        if ($rootScope.cartItems.length == 1) {
            if (window.device) {
                navigator.notification.confirm(
                    'You have only one item in your cart.\nDo you want to remove ?',  // message
                    onConfirm,              // callback to invoke with index of button pressed
                    '',            // title
                    'Yes,No'          // buttonLabels
                );

            } else {
                var r = confirm("You have only one item in your cart.\nDo you want to remove ?");
                if (r == true) {
                    $rootScope.cartItems = [];
                    $scope.cartItems = [];
                    $location.path("/app/BigDeal/BigDealPage8");
                    $scope.$apply();
                } else {

                }
            }
        } else {
            $rootScope.cartItems.splice(index, 1);
            calculate();

        }

    };
    function onConfirm(buttonIndex) {
        if (buttonIndex == 1) {
            $rootScope.cartItems = [];
            $scope.cartItems = [];
            $location.path("/app/BigDeal/BigDealPage8");
            $scope.$apply();
        } else {

        }

    }

    $scope.itemSelected = function () {
        $rootScope.payableamount = $scope.payableamount;
        $rootScope.subtotal = $scope.subtotal;
        $rootScope.discount = $scope.discount;

        $rootScope.discountamount = $scope.discountamount;

        $rootScope.vatAmount = $scope.vatAmount;
        $rootScope.serviceTaxAmount = $scope.serviceTaxAmount;
        $rootScope.serviceChargeAmount = $scope.serviceChargeAmount;



        $location.path("/app/BigDeal/BigDealPage12");


    }


}

