        try {
            var time = String(timeR);
            var min = time.split(":")[1];
            var nowTime = new Date();
            if (time.indexOf("PM") != -1)
                var endTime = new Date(nowTime.getFullYear(), nowTime.getMonth(), nowTime.getDate(), parseInt(time.slice(0, 2)) + 12, parseInt(min.slice(0, 2)), 0, 0);
            else
                var endTime = new Date(nowTime.getFullYear(), nowTime.getMonth(), nowTime.getDate(), parseInt(time.slice(0, 2)), parseInt(min.slice(0, 2)), 0, 0);

            var diff = endTime.getTime() - nowTime.getTime();


            var minutes = ((diff / (1000 * 60)) % 60);

            var hours = ((diff / (1000 * 60 * 60)) % 24);
            timeRemain = Math.floor(hours) + "h:" + Math.floor(minutes) + "m";

        } catch (e) {
            timeRemain = e;
