function pageprocessorChangePassword(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);
  $rootScope.back = function() {
    $location.path("/app/Settings2/Settings2");
  }
  $scope.changePassword = function() {
    if ($scope.currentpwd == "" || $scope.currentpwd == undefined) {
      if (window.device) {
        navigator.notification.alert("Please enter your current password.", null, 'Alert', 'Ok');
      } else {
        alert("Please enter your current password.");
      }
      return;
    }
    if ($scope.pwd == "" || $scope.pwd == undefined) {
      if (window.device) {
        navigator.notification.alert("Please enter password.", null, 'Alert', 'Ok');
      } else {
        alert("Please enter password.");
      }
      return;
    }
    if ($scope.cpwd == "" || $scope.cpwd == undefined) {
      if (window.device) {
        navigator.notification.alert("Please enter confirm password.", null, 'Alert', 'Ok');
      } else {
        alert("Please enter confirm password.");
      }
      return;
    }
    if ($scope.pwd === $scope.cpwd) {

    } else {
      if (window.device) {
        navigator.notification.alert("Password doesn't match", null, 'Alert', 'Ok');
      } else {
        alert("Password doesn't match", null, 'Alert', 'Ok');
      }
      return;
    }
    $.blockUI({
      message: '<div id="floatingBarsG"><div class="blockG" id="rotateG_01"></div><div class="blockG" id="rotateG_02"></div><div class="blockG" id="rotateG_03"></div><div class="blockG" id="rotateG_04"></div><div class="blockG" id="rotateG_05"></div><div class="blockG" id="rotateG_06"></div><div class="blockG" id="rotateG_07"></div><div class="blockG" id="rotateG_08"></div></div><div></div>'
    });

    var url = "https://kryptosda.kryptosmobile.com/kryptosds/nuser/changepassword";
    var tenantid = $rootScope.metadata.tenantid;

    var username = $rootScope.useremail;


    var data = {
      "tenant": tenantid,
      "username": username,
      "oldpwd": $scope.currentpwd,
      "pwd": $scope.pwd,
      cpwd: $scope.cpwd
    };


    $http.post(url, data).success(function(res) {
      $.unblockUI();

      if (res.success) {
        if (window.device) {
          navigator.notification.alert("Password updated.", null, 'Successful', 'Ok');
        } else {
          alert("Password updated.");
        }
      } else {
        if (window.device) {
          navigator.notification.alert(res.error, $scope, null, 'Error', 'Ok');
        } else {
          alert(res.error);
        }
      }
    }).error(function(err) {
      $.unblockUI();
      if (window.device) {
        navigator.notification.alert("Some error occurred. Please try after sometime.", $scope, null, 'Error', 'Ok');
      } else {
        alert("Some error occurred. Please try after sometime.");
      }
    });
